import React, {Component} from 'react';
import {View, Text, TouchableOpacity, StyleSheet, Image} from 'react-native';

class Card extends Component{
  constructor(props) {
    super(props);
    this.state = {
        cuenta:0,
        styles: StyleSheet.create({
          container:{
            width:"100%",
            margin:5,
            border:'black 1 solid'
        },
        texto:{
            fontSize:18,
            textAlign:'center'
        },
        boton:{
            borderRadius: 5,
            backgroundColor: 'lightgreen',
            padding: 3,
            textAlign: 'center',
            
        },
        image:{
            height:200
        }
        })
    };
  }

  render() {
      console.log(this.props);
    return (
        <View style={this.state.styles.container}>
            <Image  style={this.state.styles.image}
                    source={{uri:this.props.props.image}}
                    resizeMode='contain'/>
            <Text style={this.state.styles.texto}>{this.props.props.name}</Text>
        </View>

      
    );
  }

}

export default Card
